import React, { Component } from 'react'

export default class Dog extends Component {
  constructor(props) {
    super(props);

    this.state = {
      loading: false,
      imagePath: '',
    };

    this.requestApi = this.requestApi.bind(this);
    this.renderLoading = this.renderLoading.bind(this);
    this.renderDogImage = this.renderDogImage.bind(this);
    this.handleClick = this.handleClick.bind(this);
  }

  componentDidMount() {
    this.requestApi();
  }

  componentDidUpdate() {
    const { imagePath } = this.state;
    alert(imagePath.split('/')[4]);
  }

  requestApi() {
    this.setState({
      loading: true,
    }, () => {
      fetch('https://dog.ceo/api/breeds/image/random')
        .then((dog => dog.json()))
          .then(({ message }) => {
            this.setState({
              imagePath: message,
              loading: false,
            });
            localStorage.setItem('dogImagePath', message);
          });
    });
  }

  async handleClick() {
    this.setState({
      loading: true,
    }, async () => {
        const request = await fetch('https://dog.ceo/api/breeds/image/random');
        const dogData = await request.json();
        const { message } = dogData;
        if(!message.includes('terrier')) {
          this.setState({
            imagePath: message,
            loading: false,
          });
          localStorage.setItem('dogImagePath', message);
        }
        this.setState({
          loading: false,
        });
      });
  }

  renderLoading() {
    return <div>Loading...</div>;
  }

  renderDogImage() {
    const { imagePath } = this.state;
    return (
      <div>
        <img src={ imagePath } alt="foto do cachorro" width="500px" height="500px"></img>
        <div>
          <button type="button" onClick={ this.handleClick }>New Dog</button>
        </div>
      </div>
    );
  }

  render() {
    const { loading } = this.state;
    return (
      <div>
        { loading ? this.renderLoading() : this.renderDogImage() }
      </div>
    )
  }
}
